        /// <summary>
        /// Gets the Gender of the Pokemon.
        /// </summary>
        /// <param name="buff"></param>
        /// <returns>A string representing the Gender of the Pokemon.</returns>
        public static string GetGender(byte[] buff)
        {
            string g = string.Empty;
            int genderflag = (buff[0x1D] >> 1) & 0x3;
            if (genderflag == 0)
            {
                // Gender = Male
                g = " (M)";
            }
            else if (genderflag == 1)
            {
                // Gender = Female
                g = " (F)";
            }
            else
            {
                g = string.Empty;
            }
            return g;
        }

        /// <summary>
        /// Gets the Nature of the Pokemon.
        /// </summary>
        /// <param name="buff"></param>
        /// <returns></returns>
        public static string GetNature(byte[] buff)
        {
            int nature = buff[0x1C];
            string[] nattable = new string[] { "Hardy", "Lonely", "Brave", "Adamant", "Naughty", "Bold", "Docile", "Relaxed", "Impish", "Lax", "Timid", "Hasty", "Serious", "Jolly", "Naive", "Modest", "Mild", "Quiet", "Bashful", "Rash", "Calm", "Gentle", "Sassy", "Careful", "Quirky" };
            return nattable[nature];
        }

